INSERT INTO tb_produtos(nome, nome_curto, valor, descricao) VALUES (
		'Câmera Nikon COOLPIX P900 16MP CMOS, 24-2000mm, 35mm, 83X Óptico Zoom, 332x Zoom Digital, Wi-Fi, NFC, GPS Preta', 
		'Nikon COOLPIX P900', 
